import { __decorate } from "tslib";
import { Component, ViewChild } from '@angular/core';
import { DataTableDirective } from 'angular-datatables';
import { Subject } from 'rxjs';
let LstpaisComponent = class LstpaisComponent {
    constructor(servPais) {
        this.servPais = servPais;
        this.dtOptions = {};
        this.dtTrigger = new Subject();
        this.misPaises = [];
    }
    ngOnInit() {
        this.dtOptions = {
            pagingType: 'full_numbers',
            pageLength: 5
        };
        this.CargarPaises();
    }
    async CargarPaises() {
        const result = await this.servPais.listarPaises();
        this.misPaises = result;
    }
    rerender() {
        this.servPais.listarPaises().then(result => {
            this.misPaises = result.data;
            this.dtElement.dtInstance.then((dtInstance) => {
                // Destroy the table first
                dtInstance.destroy();
                // Call the dtTrigger to rerender again
                this.CargarPaises();
            });
        });
    }
};
